import { createSlice,nanoid } from "@reduxjs/toolkit";

const initialState = {
    details:[]
}

export const studentSlice = createSlice({
    name:"students",
    initialState,
    reducers:{
        addStudent:(state,action)=>{
            if(Array.isArray(state.details)){
                   state.details = [] ; 
            }
            const student = {id:nanoid(),name:action.payload.name,mark:action.payload.mark};
            state.details.push(student);
        },
        deleteStudent:(state,action)=>{
            state.details = state.details.filter(student => student.id !== action.payload.id); 
        }
    }
})

export const {addStudent,deleteStudent} = studentSlice.actions

export default studentSlice.reducer
